import axios from "axios";
import { useState } from "react"
import { useNavigate } from "react-router-dom";


export default function AddDiscuss() {
    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [type, setType] = useState("");
    const navigate = useNavigate();

    const onSubmit = async (event) => {
        event.preventDefault();

        try {
            await axios.post("http://localhost:3001/discuss/add", {
                title, content, type
            });
            navigate("/discuss");
        } catch (err) {
            console.error(err)
        }
    }

    return <div>
        <form onSubmit={onSubmit}>
            <h2>Add a Discussion</h2>
            <input type="text" onChange={(event) => setTitle(event.target.value)} name="title" value={title} placeholder="Title..."></input>
            <input type="text" name="content" onChange={(event) => setContent(event.target.value)} value={content} placeholder="Content..."></input>
            <details role="list" value={type} onChange={(event) => setType(event.target.value)}>
                <summary aria-haspopup="listbox">Type</summary>
                <ul role="listbox">
                    <li><a>General</a></li>
                    <li><a>Question</a></li>
                    <li><a>Fun</a></li>
                </ul>
            </details>
            <button type="submit">Add</button>
        </form>
    </div>
}
